package Lesson1;

/**
 * @author User
 *
 */
public class IOAO {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=10;
		x+=5;
		System.out.println("Incremented Value Is = "+x);
		x++;
		System.out.println("Incremented Value Is = "+x);
		x-=5;
		System.out.println("Incremented Value Is = "+x);
		x--;
		System.out.println("Incremented Value Is = "+x);
		x*=5;
		System.out.println("Incremented Value Is = "+x);
		x/=5;
		System.out.println("Incremented Value Is = "+x);

	}

}
